import numbers
import runtime
import word
import datetime
import json

class WordDemoLib:
    @staticmethod
    def insertPictureAtEnd(context: word.RequestContext, base64ImageData: str):
        context.document.body.insertInlinePictureFromBase64(base64ImageData, word.InsertLocation.end);
        context.sync();
        return

    @staticmethod
    def insertSamplePictureAtEnd(context: word.RequestContext):
        sampleImageData = ""
        WordDemoLib.insertPictureAtEnd(context, sampleImageData)
        return
